def create_inline_keyboard(exclude_option=None):
        markup = types.InlineKeyboardMarkup(row_width=1)
        buttons = [
            types.InlineKeyboardButton(text="–ü—Ä–æ–π—Ç–∏ —Ç–µ—Å—Ç –∑–∞–Ω–æ–≤–æ", callback_data="restart"),
            types.InlineKeyboardButton(text="–£–∑–Ω–∞—Ç—å –∏–Ω—Ç–µ—Ä–µ—Å–Ω—ã–µ —Ñ–∞–∫—Ç—ã", callback_data="coffee_facts"),
            types.InlineKeyboardButton(text="–£–∑–Ω–∞—Ç—å –æ—Å–æ–±–µ–Ω–Ω–æ—Å—Ç–∏ –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è –≤ —Ä–∞–∑–Ω—ã—Ö —Å—Ç—Ä–∞–Ω–∞—Ö",
                                       callback_data="coffee_countries")
        ]

        # –ò—Å–∫–ª—é—á–∞–µ–º –Ω–∞–∂–∞—Ç—É—é –∫–Ω–æ–ø–∫—É
        for button in buttons:
            if button.callback_data != exclude_option:
                markup.add(button)

        return markup


